function setCookie(e,o,i){var s=new Date;s.setTime(s.getTime()+24*i*60*60*1e3);var t="expires="+s.toUTCString();document.cookie=e+"="+o+";"+t+";path=/"}function getCookie(e){for(var o=e+"=",i=document.cookie.split(";"),s=0;s<i.length;s++){for(var t=i[s];" "==t.charAt(0);)t=t.substring(1);if(0==t.indexOf(o))return t.substring(o.length,t.length)}return""}function checkCookie(){switch(getCookie("ipAddress")){case"US":setCookie("modalCheck",!1,1);break;case"":ipAddressCheck();break;default:shippingModal(getCookie("modalCheck"))}}function shippingModal(e){switch(e){case"true":$("#shipping_message").css({display:"block"});break;case"false":break;default:setCookie("modalCheck",!0,1)}}function shippingClose(){$("#shipping_message").css({display:"none"}),setCookie("modalCheck",!1,1)}function ipAddressCheck(){$.get("https://ipinfo.io",function(e){setCookie("ipAddress",e.country,1),checkCookie()},"jsonp")}checkCookie();